const observer=new IntersectionObserver(e=>{e.forEach(e=>{e.isIntersecting&&e.target.classList.add("visible")})});document.querySelectorAll("[data-animate]").forEach(e=>{observer.observe(e)});const backToTop=document.querySelector(".back-to-top");window.addEventListener("scroll",()=>{backToTop.classList.toggle("show",window.scrollY>300)});